var ModelCollection=function(){this.collection=[]};ModelCollection.prototype={add:function(a){this.collection.push(a);return true},all:function(){return this.collection},find:function(a){return _.detect(this.collection,function(b){return b.id()==a})||null},first:function(){return this.collection[0]||null},remove:function(a){var b=_.pluck(this.collection,"id");a=_.indexOf(b,a);if(a>-1){this.collection.splice(a,1);return true}else return false}};
